import profiles from "../data/profileList.json";
import getHcsLink from "./getHcsLink";

export default function getUserData() {
  let profileList = [];
  
  profiles['users'].forEach((user, i) => {
    const data = require(`../data/users/${user}.json`);

    profileList.push([
      {
        type: "id",
        index: "User ID",
        data: function() {
          try {
            return !data.data.userid
            ? ""
            : data.data.userid
          } 
          catch {return ""}
        }()
      },
      {
        type: "display",
        index: "Display",
        data: function() {
          try {
            return !data.data.displayName
            ? ""
            : data.data.displayName
          } 
          catch {return ""}
        }()
      },
      {
        type: "primary",
        index: "Primary Line",
        data: function() {
          try {
            return !data.data.primaryExtension.pattern
            ? ""
            : data.data.primaryExtension.pattern
          } 
          catch {return ""}
        }()
      },
      {
        type: "department",
        index: "Department",
        data: function() {
          try {
            return !data.data.title
            ? ""
            : data.data.title
          } 
          catch {return ""}
        }()
      },
      {
        type: "device",
        index: "MAC Address",
        data: function() {
          try {
            return !data.data.Phone[0].name
            ? ""
            : data.data.Phone[0].name
          } 
          catch {return ""}
        }()
      },
      {
        type: "location",
        index: "Location",
        data: function() {
          try {
            return !data.meta.hierarchy.split("_").pop()
            ? ""
            : data.meta.hierarchy.split("_").pop()
          } 
          catch {return ""}
        }()
      },
      {
        type: "",
        index: "",
        data: ""
      },
      {
        type: "link",
        index: "Link",
        data: getHcsLink(data.data.userid, "users")
      }
    ]);
  });

  return profileList;
};
