import * as style from "../../css/searchList";

export default function handleSearchTypeChange(targ, data, ret=['Device', 'User', 'DID', 'borderWidth']) {

  const test = {
    'case': {
      'device': [1,0,0],
      'user': [0,1,0],
      'did': [0,0,1],
      'borderWidth': [0,0,0]
    },
    "true": {
      'device': "searchTypeDeviceStyleHighlight",
      'user': "searchTypeUserStyleHighlight",
      'did': "searchTypeDidStyleHighlight"
    },
    'false': {
      'device': "searchTypeDeviceStyle",
      'user': "searchTypeUserStyle",
      'did': "searchTypeDidStyle"
    }
  }
  
  
  let displayStyle = {}
  let display = {}
  let results = {}
  
  ret.forEach((type, i) => {
    if (targ.target.textContent === `${type}` && !targ.target.style['borderWidth']) {
      // console.log(Object.keys(test['case'])[i])
      let bool = test['case'][type.toLowerCase()]
      bool.forEach((value, i) => {
        results[Object.keys(test[Boolean(value)])[i]] = [Boolean(value), test[Boolean(value)][Object.keys(test[Boolean(value)])[i]]]
      })
    }
  })

  console.log(results)

  if (targ.target.textContent === "Device" && !targ.target.style["borderWidth"]) {
    return ([
      displayStyle = ({"device": style.searchTypeDeviceStyleHighlight, "user": style.searchTypeUserStyle, "did": style.searchTypeDidStyle}),
      display = ({"device": true, "user": false, "did": false})
    ])
  }

  if (targ.target.textContent === "User" && !targ.target.style["borderWidth"]) {
    return ([
      displayStyle = ({"device": style.searchTypeDeviceStyle, "user": style.searchTypeUserStyleHighlight, "did": style.searchTypeDidStyle}),
      display = ({"device": false, "user": true, "did": false})
    ])
  }

  if (targ.target.textContent === "DID" && !targ.target.style["borderWidth"]) {  
    return ([
      displayStyle = ({"device": style.searchTypeDeviceStyle, "user": style.searchTypeUserStyle, "did": style.searchTypeDidStyleHighlight}),
      display = ({"device": false, "user": false, "did": true})
    ])
  }

  if (targ.target.style["borderWidth"]) {
    return ([
      displayStyle = ({"device": style.searchTypeDeviceStyle, "user": style.searchTypeUserStyle, "did": style.searchTypeDidStyle}),
      display = ({"device": false, "user": false, "did": false})
    ])
  }
  return [displayStyle, display]
}
