// import returnSearch from "./returnSearch"
import getDeviceData from "../dataRetrieval/getDeviceData";
import getUserData from "../dataRetrieval/getUserData";

export default function returnSearch(string, listToRender) {
  let temp = {}

  listToRender.forEach(list => {
    switch(list) {
      case "Device":
        temp[e.toLowerCase()] = returnSearch(getDeviceData, string.target.value.toLowerCase()); 
        break
      case "User": 
        temp[e.toLowerCase()] = returnSearch(getUserData, string.target.value.toLowerCase()); 
        break
      default: break
    } 

  })
  return temp
}
