import React, {useRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  console.log(props.display, props.total)
  const [total, setTotal] = useState(props.total.device)
  const [pageStyle, setPageStyle] = useState(style.pageNumber)
  const [visible, setVisible] = useState([2, 3, 4, 5, 6])
  const leftTab = useRef(null);
  const rightTab = useRef(null);

  const handlePageNumber = () => {
    let old = props.total
    let temp = []
    let highlighted = false

    for (const property in props.display) {
      if (props.display[property] === false) {
        highlighted = true
        break
      }
    }

    if (highlighted) {
      for (const property in props.display) if (props.display[property] === true) {
        temp = old[property]
        console.log(property + " highlighted")
      }
      return setTotal(temp)
    }

    else {
      for (const property in old) if (temp < old[property]) {
        temp = old[property]
        console.log("none highlighted")
      }
      return setTotal(temp)
    }
  }
    
  const handleClick = (e) => {
    let page = parseInt(e.target.id)
    console.log(page)
    if (page > total-2 || (page === total-2 && visible[4] !== total-2)) {
      //highlight number
      return
    }

    if (page < 3) {
    //highlight number
      return
    }

    if (page === total-2 && visible[4] === total-2) setVisible([page-3, page-2, page-1, page, page+1])
    else if (page === 3 && visible[0] === 3) setVisible([page-1, page, page+1, page+2, page+3])
    else setVisible([page-2, page-1, page, page+1, page+2, total])

    page <= 4 ? leftTab.current.style.visibility = "hidden" : leftTab.current.style.visibility = "visible"
    page <= total-4 ? rightTab.current.style.visibility = "hidden" : rightTab.current.style.visibility = "visible"
  }

  const handleFirstPageClick = (e) => {
    props.pageNumber(e.currentTarget.textContent)
    setVisible([2,3,4,5,6, total])
    leftTab.current.style.visibility = "hidden"
    rightTab.current.style.visibility = "visible"
  }

  const handleLastPageClick = (e) => {
    props.pageNumber(e.currentTarget.textContent)
    setVisible([total-5, total-4, total-3, total-2, total-1, total])
    leftTab.current.style.visibility = "visible"
    rightTab.current.style.visibility = "hidden"
  }

  const renderPageList = () => {
    return visible.map((i, item) => (
        <div key={`Page${i}`} id={i} onClick={e => handleClick(e)} style={pageStyle}>{i}</div>
      )
    )
  }

  return (
    <div onClick={() => handlePageNumber()} style={{display: "flex"}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          <div id={1} onClick={(e) => handleFirstPageClick(e)} style={style.firstPage}>{`${total - (total - 1)}`}</div>
          <div ref={leftTab} style={style.leftTab}>|</div>
          {renderPageList()}
          <div ref={rightTab} style={style.rightTab}>|</div>
          <div id={total} onClick={(e) => handleLastPageClick(e)} style={style.lastPage}>{`${total}`}</div>
        </div>
      <div style={style.rightArrow}/>
    </div>
  )
}
