import profiles from "../../data/allstream_profile_list.json"
import validateString from "./validateString";
import getHcsLink from "./getHcsLink";

export default function getUserData() {
  let profileList = [];
  
  profiles['users'].forEach((user, i) => {
    const data = require(`../../data/users/${user}.json`)

    profileList.push([
      {
        key: i,
        type: "id",
        index: "User ID",
        data: () => {try {return data.resources[0].data.userid} catch {return ""}}
        // data: validateString(data.resources[0].data.userid)
      },
      {
        key: i,
        type: "display",
        index: "Display",
        data: () => {try {return data.resources[0].data.displayName} catch {return ""}}
        // data: validateString(data.resources[0].data.displayName)
      },
      {
        key: i,
        type: "primary",
        index: "Primary Line",
        data: () => {try {return data.resources[0].data.primaryExtension.pattern} catch {return ""}}
        // data: validateString(data.resources[0].data.primaryExtension.pattern)
      },
      {
        key: i,
        type: "department",
        index: "Department",
        data: () => {try {return data.resources[0].data.title} catch {return ""}}
        // data: validateString(data.resources[0].data.title)
      },
      {
        key: i,
        type: "device",
        index: "MAC Address",
        data: () => {try {return data.resources[0].data.Phone[0].name} catch {return ""}}
        // data: validateString(data.resources[0].data.Phone[0].name)
      },
      {
        key: i,
        type: "location",
        index: "Location",
        data: () => {try {return data.resources[0].meta.hierarchy.split("_").pop()} catch {return ""}}
        // data: validateString(data.resources[0].meta.hierarchy.split("_").pop())
      },
      {
        key: i,
        type: "",
        index: "",
        data: ""
      },
      {
        key: i,
        type: "link",
        index: "Link",
        data: getHcsLink(data.resources[0].data.userid, "users")
      }
    ])
  })

  console.log(profileList)
  return profileList
}

getUserData()
