import * as style from "../../css/light/searchList";
import createButtonState from "./createButtonState";
import React, {useEffect, useState} from 'react';

export default function ProfileButtons(props) {
  const staticButtons = props.listToRender
  const [rerender, setRerender] = useState(false)
  const [buttonsToRender] = useState(staticButtons)
  const [buttonStyle, setButtonStyle] = useState(createButtonState(buttonsToRender, style))
  let check = ""

  useEffect(() => {
    if (check === props.listToRender) {/*pass*/}
    else {
      check = props.listToRender
      setRerender(true)
      console.log("updated")
    }
  }, [props.listToRender])

  const changeFilter = (item, position) => {
    buttonStyle[position].border 
    ? setButtonStyle({...buttonStyle, [position]: style.searchTypeOther})
    : setButtonStyle({...buttonStyle, [position]: style.searchTypeOtherHighlight})

    if (props.listToRender.includes(item.target.innerHTML)) {
      let temp = props.listToRender
      temp = temp.filter((e) => {return e !== item.target.innerHTML})
      return props.setListToRender(temp)
    }

    let temp = props.listToRender
    temp.push(item.target.innerHTML)
    props.setListToRender(temp.sort())
  }

  const renderButtons = () => {
    return buttonsToRender.map((item, i) => (
      <div key={`B${i}`} onClick={(e) => changeFilter(e, i)} style={buttonStyle[i]}>
        {item}
      </div>
    ))
  }

  return (
    <div style={{display: "flex", marginBottom: "5px"}}>
      {renderButtons()}
    </div>
  )
}
