import React, {useCallback, useState} from "react"
import * as style from "./style/css"

function ProfileTotalItem({item, style, profileDataToRender}) {
  return (
    <div style={style}>
      {`${item}: ${profileDataToRender[item.toLowerCase()]?.length}`}
    </div>
  )
}

export default function ProfileTotal({totalToRenderNumber, setTotalToRenderNumber, profileDataToRender, listToRender}) {
  const [userInputRecord, setUserInputRecord] = useState("")


  // Event listener to check items/page value on user input // 
  const onKeyPress = useCallback(e => {
    if (e.key === "Enter") setTotalToRenderNumber(userInputRecord)
  }, [setTotalToRenderNumber, userInputRecord])


  // Renders each total item //
  // What's rendered is dependent on the listToRender state //
  const renderTotalItems = () => {
    return listToRender?.map(item => (
      <ProfileTotalItem
        item={item}
        style={style.totalBarItem}
        profileDataToRender={profileDataToRender}/>
    ))
  }


  // Renders items/page element //
  const renderItemsPerPage = () => {
    return (
      <div style={{display: "flex", marginLeft: "auto", paddingRight: "3px"}}>
        <div style={style.totalTitleOther}>Items/Page</div>

        {/* Renders input element for items/page referencing onKeyPress */}
        <input 
            placeholder={totalToRenderNumber} 
            style={style.totalInput} 
            onChange={e => setUserInputRecord(parseInt(e.target.value))}
            onKeyPress={onKeyPress}/>

        {/* Renders the enter button for items/page */}
        <div 
            style={style.totalButton}
            onClick={() => setTotalToRenderNumber(userInputRecord)}>
          <div style={style.totalButtonLine}></div>  
        </div>

      </div>
    )
  }


  // Renders all components of ProfileTotal //
  return (
    <div style={style.totalContainer}>
      <div style={style.totalTitleMain}>Total Count:</div>
      {renderTotalItems()}
      {renderItemsPerPage()}
    </div>
  )
}
