let container = {
    zIndex: 5,
    display: 'flex',
    width: '25%',
    height: 30,
    margin: "10px 0 0 auto",
    flexWrap: 'wrap',
    userSelect: "none",
}

let menuContainer = {
    display: 'flex',
    width: 304,
    marginLeft: 'auto'
}

let ddlContainer = {
    width: 304,
    height: 400,
    marginLeft: 'auto',
    backgroundColor: "#063b50",
    borderRadius: '0 0 5px 5px',
    transition: 'all 0.5s'
}

let ddlOpen = {
    // transition: 'all 0.5s',
    maxHeight: 400,
    // visibility: 'visible'
}

let ddlClose = {
    // transition: 'all 0.5s',
    maxHeight: 0,
    // visibility: 'hidden'
}

let ddlIndicator = {
    position: 'relative',
    top: 3,
    width: 80,
    height: 1,
    backgroundColor: 'white',
    opacity: 0,
    transition: 'opacity 0.5s, left 0.3s'
}

let navContent = {
    opacity: 0,
    visibility: 'hidden',
    transition: 'all 0.3s'
}

let alertContent = {
    opacity: 0,
    visibility: 'hidden'
}

let settingContent = {
    opacity: 0,
    visibility: 'hidden'
}

export {
    container,
    menuContainer,
    ddlContainer,
    ddlOpen,
    ddlClose,
    ddlIndicator,
    navContent,
    alertContent,
    settingContent
}
