import {React, useState} from 'react';
import * as __data from "./searchListData"
import * as style from "../../css/searchList";
import renderIndexMenu from './renderIndexMenu';
import getItem from './getItem';
import getTotalCount from './getTotalCount';
import getDisplayStyle from './getDisplayStyle';
import renderList from './renderList';
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";
import getDidData from "./getDidData";
import getSearch from './getSearch';
import getFilteredSearch from './getFilteredSearch';
import Pages from "../pages";
import handleSearchTypeChange from "./handleSearchTypeChange";

let filteredSearch = {
  "device": {"results": getDeviceData, "relevant": "mac"}, 
  "user": {"results": getUserData, "relevant": "id"},
  "did": {"results": getDidData, "relevant": "did"}
}

// function test(filteredSearch, data, ret=['device', 'user', 'did']) {
//   ret.forEach(e => {
//     data['default']['display'][e]['total'] = Math.ceil(filteredSearch[e]['results'].length/100)
//   })
//   return data
// }

export default function SearchList() {
  const [data, setData] = useState(__data['default'])
  const [item, setItem] = useState(getItem(filteredSearch))
  const [displayStyle, setDisplayStyle] = useState(getDisplayStyle(style))
  const [pageNumber, setPageNumber] = useState(1)
  let totalCount = getTotalCount(filteredSearch)
  let defaultType = displayStyle

  // console.log(displayStyle)

  const handleInputChange = string => {
    let search = getSearch(getDeviceData, getUserData, getDidData, string, filteredSearch)
    filteredSearch = getFilteredSearch(search)
    totalCount = (getTotalCount(filteredSearch))
    setItem(getItem(filteredSearch))
  }

  // const handlePageNumberChange = e => {
  //   setPageNumber(e)
  //   return pageNumber
  // }

  // const getSearchTypeChange = (e) => {
  //   let search = handleSearchTypeChange(e, data, displayStyle)
  //   setData(search[0])
  //   setDisplayStyle(search[1])
  //   return t





  // }

  const getSearchTypeChange = (e) => {
    let targ = e.target.innerHTML.toLowerCase()
    setDisplayStyle(defaultType)
    
    if (targ === 'device' && !e.target.style['borderWidth']) {
      console.log('device')
      !e.target.style['borderWidth'] ? 
      setDisplayStyle({...displayStyle, device: style.searchTypeDeviceStyleHighlight}) : 
      setDisplayStyle({...displayStyle, device: style.searchTypeDeviceStyle})
      return  
    }
    
    if (targ === 'user' && !e.target.style['borderWidth']) {
      console.log('user')
      !e.target.style['borderWidth'] ? 
      setDisplayStyle({...displayStyle, user: style.searchTypeUserStyleHighlight}) : 
      setDisplayStyle({...displayStyle, user: style.searchTypeUserStyle})
      return
    }
    
    if (targ === 'did' && !e.target.style['borderWidth']) {
      console.log('did')
      !e.target.style['borderWidth'] ? 
      setDisplayStyle({...displayStyle, did: style.searchTypeDidStyleHighlight}) : 
      setDisplayStyle({...displayStyle, did: style.searchTypeDidStyle})
      return  
    }

    // if (e.target.style['borderWidth']) {
      console.log('else')
      setDisplayStyle({
        'device': style.searchTypeDeviceStyle,
        'user': style.searchTypeUserStyle,
        'did': style.searchTypeDidStyle
      }) 
      return
    // }

  }

  return (
    <div style={{minWidth: "59.5vw"}}>
      <div style={{display: "flex", minWidth: "59.5vw"}}>
        <div style={{display: "flex"}}>
          <div onClick={e => getSearchTypeChange(e)} style={displayStyle['device']}>Device</div>
          <div onClick={e => getSearchTypeChange(e)} style={displayStyle['user']}>User</div>
          <div onClick={e => getSearchTypeChange(e)} style={displayStyle['did']}>DID</div>
        </div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <div >
        <div style={style.listContainerStyle}>
          {/* <div>{renderIndexMenu(data['display']['device'], item['device'], filteredSearch['device']['relevant'], data.index.device)}</div>
          {renderList(item.device, data['display']['device'], pageNumber)}
          <div>{renderIndexMenu(data['display']['user'], item['user'], filteredSearch.user.relevant, data.index.user)}</div>
          {renderList(item.user, data['display']['user'], pageNumber)}
          <div>{renderIndexMenu(data['display']['did'], item['did'], filteredSearch.did.relevant, data.index.did)}</div>
          {renderList(item.did, data['display']['did'], pageNumber)} */}
        </div>
        {/* <Pages data={data} page={pageNumber} display={display} total={totalCount} pageNumber={handlePageNumberChange}/> */}
      </div>
    </div>
  );
};
