import { useState } from 'react'
import Alerts from './alerts/alerts'
import Navigation from './navigation/navigation'
import Settings from './settings/settings'

let optionsContainer = {
    zIndex: 5,
    display: 'flex',
    width: '35%',
    height: 30,
    margin: "10px 0 0 auto",
    flexWrap: 'wrap',
    userSelect: "none",
}

let optionsMenuContainer = {
    display: 'flex',
    width: 304,
    marginLeft: 'auto'
}

let optionsDdlContainer = {
    width: 400,
    height: 400,
    // maxHeight: 0,
    marginLeft: 'auto',
    backgroundColor: "#063b50",
    borderRadius: '5px 0 5px 5px',
    // transition: 'max-height 0.5s'
}

let optionsDdlIndicator = {
    position: 'relative',
    top: 3,
    width: 80,
    height: 1,
    backgroundColor: 'white',
    opacity: 0,
    transition: 'opacity 0.5s, left 0.3s'
}

let ddlOpen = {
    transition: 'max-height 0.5s',
    maxHeight: 0
}

let ddlClose = {
    transition: 'max-height 0.5s',
    maxHeight: 400
}

export default function Options() {
    const [ddlContainer, setDdlContainer] = useState(optionsDdlContainer)
    const [ddlIndicator, setDdlIndicator] = useState(optionsDdlIndicator)
    const [ddlContent, setDdlContent] = useState()
    const [currentId, setCurrentId] = useState('')

    function handleClick(id, pos) {
        if (id === currentId) {
            setDdlContainer({...ddlContainer, maxHeight: 0})
            setDdlIndicator({...ddlIndicator, opacity: 0})
            // setCurrentId('')
        } else {
            setDdlContainer({...ddlContainer, maxHeight: 400})
            setDdlIndicator({
                ...ddlIndicator, 
                opacity: 1,
                left: 107 + (101*pos)})
            setCurrentId(id)
        }
    }

    function handleMouseLeave() {
        setDdlContainer({...ddlContainer, maxHeight: 0})
        setDdlIndicator({...ddlIndicator, opacity: 0})
        // setCurrentId('')
    }

    let navButton = {
        display: 'block',
        width: '90%',
        height: 30,
        margin: '20px auto 0 auto',
        borderRadius: 5,
        backgroundColor: 'blue',
        textAlign: 'center',
        paddingTop: 7,
        cursor: 'pointer',
        textDecoration: 'none',
        color: 'white',
        fontWeight: 'bold'
    }

    function renderDdlContent() {
        switch(currentId) {
            case 'nav':
                return (
                    <span>
                        <a style={navButton} href='/ld-tickets'>ISD Tickets</a>
                    </span>
                )

            case 'alert':
                return (
                    <div>This is ALERT</div>
                )

            case 'setting':
                return (
                    <div>This is SETTING</div>
                )

            default: return <div></div>
        }


    }
    
    return (
        <span onMouseLeave={handleMouseLeave} style={optionsContainer}>
            <span style={optionsMenuContainer}>
                <div onClick={() => handleClick('nav', 0)}><Navigation/></div>
                <div onClick={() => handleClick('alert', 1)}><Alerts/></div>
                <div onClick={() => handleClick('setting', 2)}><Settings/></div>
            </span>

            <span style={ddlContainer}>
                <div style={ddlIndicator}></div>
                {renderDdlContent()}
            </span>
        </span>
    )
}
