import React, {useRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total
  const [pageStyle, setPageStyle] = useState(style.pageNumber)
  const [visible, setVisible] = useState([2, 3, 4, 5, 6])
  const leftTab = useRef(null);
  const rightTab = useRef(null);

  const handleClick = (e) => {
    let page = parseInt(e.target.id)
    if (page > total.device-2 || (page === total.device-2 && visible[4] !== total.device-2)) {
      //highlight number
      return
    }

    if (page < 3) {
    //highlight number
      return
    }

    console.log(page, total.device-2, visible[4])
    console.log(page === total.device-2 && visible[4] === total.device-2)

    if (page === total.device-2 && visible[4] === total.device-2) setVisible([page-3, page-2, page-1, page, page+1])
    else if (page === 3 && visible[0] === 3) setVisible([page-1, page, page+1, page+2, page+3])
    else setVisible([page-2, page-1, page, page+1, page+2])

    page <= 4 ? leftTab.current.style.visibility = "hidden" : leftTab.current.style.visibility = "visible"
    page <= total.device-4 ? rightTab.current.style.visibility = "visible" : rightTab.current.style.visibility = "hidden"
  }

  const handleFirstPageClick = () => {
    setVisible([2,3,4,5,6])
    leftTab.current.style.visibility = "hidden"
    rightTab.current.style.visibility = "visible"
  }

  const handleLastPageClick = () => {
    setVisible([total.device-5, total.device-4, total.device-3, total.device-2, total.device-1])
    leftTab.current.style.visibility = "visible"
    rightTab.current.style.visibility = "hidden"
  }

  const renderPageList = () => {
    return visible.map((i, item) => (
        <div key={`Page${i}`} id={i} onClick={e => handleClick(e)} style={pageStyle}>{i}</div>
      )
    )
  }

  return (
    <div style={{display: "flex"}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          <div id={1} onClick={() => handleFirstPageClick()} style={style.firstPage}>{`${total.device - (total.device - 1)}`}</div>
          <div ref={leftTab} style={style.leftTab}>|</div>
          {renderPageList()}
          <div ref={rightTab} style={style.rightTab}>|</div>
          <div id={total.device} onClick={() => handleLastPageClick()} style={style.lastPage}>{`${total.device}`}</div>
        </div>
      <div style={style.rightArrow}/>
    </div>
  )
}
