import React, {createRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total
  let pageNumber = {
    width: "16px",
    marginRight: "10px", 
    cursor: "pointer", 
    marginTop: "8px", 
    color: "#4382BC"
  }

  let lastPage = {
    width: "16px",
    marginRight: "10px",
    cursor: "pointer", 
    marginTop: "8px", 
    color: "#4382BC"
  }

  let tab = {
    margin: "6px 7px 0px -8px",
    color: "#4382BC"
  }

  let pageNumberHighlight = {
    width: "16px",
    marginRight: "10px", 
    cursor: "pointer", 
    marginTop: "80px", 
    color: "white"
  }
  const [pageStyle, setPageStyle] = useState(pageNumber)
  const [pageStyleHighlight, setPageStyleHighlight] = useState(pageNumberHighlight)
  const [visible, setVisible] = useState([1, 2, 3, 4, 5, 6])

  const handleClick = (e) => {
    e.target.style.visibility === "hidden" ? e.target.style.visibility = "visible" : e.target.style.visibility = "hidden"
  }

  const renderPageList = () => {
    let pages = []

    for (let i in visible) {
      pages.push(<div key={`Page${visible[i]}`} id={visible[i]} onClick={(e) => handleClick(e)} style={pageStyle}>{visible[i]}</div>)
    }
    return pages
  }

  return (
    <div style={{display: "flex"}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          {renderPageList()}
          <div style={tab}>|</div>
          <div onClick={(e) => handleClick(e)} style={lastPage}>
            {`${total.device}`}
          </div>
        </div>
      <div style={style.rightArrow}/>
    </div>
  )
}
