import {useRef, React, useState} from 'react';
import * as data from "./searchListData"
import * as style from "../../css/searchList";
import renderIndexMenu from './renderIndexMenu';
import getItem from './getItem';
import getTotalCount from './getTotalCount';
import getDisplayStyle from './getDisplayStyle';
import renderList from './renderList';
import returnSearch from './returnSearch';
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";
import getDidData from "./getDidData";
import Pages from "../pages";
import handleSearchTypeChange from "./handleSearchTypeChange";

let filteredSearch = {
  "device": {"results": getDeviceData, "relevant": "mac"}, 
  "user": {"results": getUserData, "relevant": "id"},
  "did": {"results": getDidData, "relevant": "did"}
}

export default function SearchList() {
  const [item, setItem] = useState(getItem(filteredSearch))
  const [totalCount, setTotalCount] = useState(getTotalCount(filteredSearch))
  const [display, setDisplay] = useState(data['display'])
  const [displayStyle, setDisplayStyle] = useState(getDisplayStyle(style))
  const [pageNumber, setPageNumber] = useState(1)
  const scrollRef = useRef({scrollTop: 0});
  
  const handleInputChange = string => {
    let search = {
      "device":returnSearch(getDeviceData, string.target.value.toLowerCase(), filteredSearch.device.results),
      "user":returnSearch(getUserData, string.target.value.toLowerCase(), filteredSearch.user.results),
      "did":returnSearch(getDidData, string.target.value.toLowerCase(), filteredSearch.did.results)
    }

    console.log(search)
    let _device = returnSearch(getDeviceData, string.target.value.toLowerCase(), filteredSearch.device.results)
    let _user = returnSearch(getUserData, string.target.value.toLowerCase(), filteredSearch.user.results)
    let _did = returnSearch(getDidData, string.target.value.toLowerCase(), filteredSearch.did.results)

    filteredSearch = {
      "device": {"results": _device[0],"relevant": _device[1]}, 
      "user": {"results": _user[0],"relevant": _user[1]},
      "did": {"results": _did[0],"relevant": _did[1]}
    }

    setTotalCount(getTotalCount(filteredSearch))
    setItem(getItem(filteredSearch))
  }

  const handlePageNumberChange = e => {
    setPageNumber(e)
    return pageNumber
  }

  const getSearchTypeChange = e => {
    let searchType = handleSearchTypeChange(e)
    setDisplayStyle(searchType[0])
    setDisplay(searchType[1])
  }

  return (
    <div style={{minWidth: "59.5vw"}}>
      <div style={{display: "flex", minWidth: "59.5vw"}}>
        <div style={{display: "flex"}}>
          <div onClick={e => getSearchTypeChange(e)} style={displayStyle.device}>Device</div>
          <div onClick={e => getSearchTypeChange(e)} style={displayStyle.user}>User</div>
          <div onClick={e => getSearchTypeChange(e)} style={displayStyle.did}>DID</div>
        </div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <div >
        <div ref={scrollRef} style={style.listContainerStyle}>
          <div>{renderIndexMenu(display.device, item.device, filteredSearch.device.relevant, data.index.device)}</div>
          {renderList(item.device, display.device, pageNumber)}
          <div>{renderIndexMenu(display.user, item.user, filteredSearch.user.relevant, data.index.user)}</div>
          {renderList(item.user, display.user, pageNumber)}
          <div>{renderIndexMenu(display.did, item.did, filteredSearch.did.relevant, data.index.did)}</div>
          {renderList(item.did, display.did, pageNumber)}
        </div>
        <Pages page={pageNumber} display={display} total={totalCount} pageNumber={handlePageNumberChange}/>
      </div>
    </div>
  );
};
