import {useState, useRef, useEffect} from "react"
import rotateArrow from "./rotateArrow"

let arrow = {
  display: "inline-block", 
  width: "5px",
  height: "5px", 
  transform: "rotate(45deg)",
  margin: "4px 0 0 5px",
  borderRight: "3px solid #4382BC", 
  borderTop: "3px solid #4382BC"
}

function renderObject(item, focusedItem, isDown) {
  // if (isDown) objectRef.current.style.display = "initial"
  if (typeof(focusedItem.resources[0].data[item]) === "object")

  // objectRef.current.style.display = "none"

  if (isDown) {
    return (
      <div>{focusedItem.resources[0].data[item]}</div>
    )
  }


  // isDown ? objectRef.current.style.display = "initial" : objectRef.current.style.display = "none"

  

}

//Extends FocusedDataList
export default function FocusedDataItem({item, focusedItem}) {
  const [isDown, setIsDown] = useState(false)
  const arrowRef = useRef(null)
  const objectRef = useRef(null)

  // useEffect(() => {
  //   objectRef.current.style.display = "none"
  // }, [objectRef])
  
  if (typeof(focusedItem.resources[0].data[item]) === "object") {
    return (
      <div
          style={{cursor: "pointer"}}
          onClick={() => {rotateArrow(isDown, setIsDown, arrowRef)}}>
        <div style={{display: "flex"}}>
          <div>{item}</div>
          <div ref={arrowRef} style={arrow}></div>
        </div>
        <div ref={objectRef} style={{textIndent: "30px"}}>{renderObject(item, focusedItem, isDown, objectRef)}</div>
      </div>
    )
  }

  else return (
    <div style={{display: "flex"}}>
      <div style={{width: "50%"}}>{item}</div>
      <div>{focusedItem.resources[0].data[item]}</div>
    </div>
  )
}
