import * as style from "../../css/light/searchList";
import createButtonState from "./createButtonState";
import React, {useState} from 'react';

export default function ProfileButtons(props) {
  const [buttons] = useState(["Device", "DID", "Line", "User"])
  const [filteredDataToRender, setFilteredDataToRender] = useState(buttons)
  const [buttonStyle, setButtonStyle] = useState(createButtonState(buttons, style))

  const addFilter = (item, position) => {
    buttonStyle[position].border 
    ? setButtonStyle({...buttonStyle, [position]: style.searchTypeOther})
    : setButtonStyle({...buttonStyle, [position]: style.searchTypeOtherHighlight})

    if (filteredDataToRender.includes(item.target.innerHTML)) {
      let temp = filteredDataToRender
      temp = temp.filter((e) => {return e !== item.target.innerHTML})
      setFilteredDataToRender(temp)
    }
    else {
      let temp = filteredDataToRender
      temp.push(item.target.innerHTML)
      setFilteredDataToRender(temp.sort())
    }
  }

  const renderButtons = () => {
    return buttons.map((item, i) => (
      <div key={`B${i}`} onClick={(e) => {addFilter(e, i); console.log(filteredDataToRender)}} style={buttonStyle[i]}>
        {item}
      </div>
    ))
  }

  return (
    <div style={{display: "flex", marginBottom: "5px"}}>
      {renderButtons()}
    </div>
  )
}



