function renderObject(selector, value, isDown, setIsDown, arrowRef) {
  if (typeof(value) === "object") {
    return (
      <div style={{cursor: "pointer"}} onClick={() => {rotateArrow(isDown, setIsDown, arrowRef)}}>
        <div style={{display: "flex"}}>
          <div>{selector}</div>
          <div ref={arrowRef} style={arrow}></div>
        </div>
      </div>
    )
  }

  else {
    return (
      <div style={{display: "flex"}}>
        <div style={{width: "50%"}}>{selector}</div>
        <div>{value}</div>
      </div>
    )
  }
}

