import React, {createRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total

  const [pageStyle, setPageStyle] = useState(pageNumber)
  const [leftTab, setleftTab] = useState(style.leftTab)
  const [rightTab, setRightTab] = useState(style.rightTab)
  const [visible, setVisible] = useState([2, 3, 4, 5, 6])

  const handleClick = (e) => {
  }

  const renderPageList = () => {
    let pages = []

    for (let i in visible) {
      pages.push(<div key={`Page${visible[i]}`} id={visible[i]} onClick={(e) => handleClick(e)} style={pageStyle}>{visible[i]}</div>)
    }
    return pages
  }

  return (
    <div style={{display: "flex"}}>  
      <div style={style.leftArrow}/>
        <div style={{display: "flex"}}>
          <div style={style.firstPage}>{`${total.device - (total.device - 1)}`}</div>
          <div style={leftTab}>|</div>
          {renderPageList()}
          <div style={rightTab}>|</div>
          <div style={style.lastPage}>{`${total.device}`}</div>
        </div>
      <div style={style.rightArrow}/>
    </div>
  )
}
