export default function getHcsLink(type, profile, location_hierarchy) {
  let link = ""
  let store = ""

  try {
    let file = require(`../data/${type}s/${profile}.json`)
    let pkid = file.meta.pkid

    
    if (file.meta.hierarchy.includes("Head Office")) store = "head_office"
    else store = "store_" + file.meta.hierarchy.split("Store ")[1]
    
    link = location_hierarchy[`link_${type}`]
    link = link.replace("{1}", location_hierarchy.stores[store])
    link = link.replace("{2}", pkid)
  }

  catch {/*catch non-existing device*/}

  return link
}
