import * as style from "../../css/light/focusedList";
// import AssociatedItem from '../searchList/associatedItem';
import FocusedTitle from "./focusedTitle"
import FocusedHistory from "./focusedHistory";

let focusedDataTitleLeft = {
  backgroundColor: "#4382BC",
  textAlign: "center",
  marginLeft: "8px",
  marginTop: "12px"
}

let focusedDataTitleRight = {
  backgroundColor: "#4382BC",
  textAlign: "center",
  marginRight: "4px",
  marginTop: "12px"
}

export default function FocusedList({focused, setFocused}) {
  if (!focused[0]) return <div></div>
  let focusedItem = require(`../../data/${focused[1]}/${focused[0]}.json`)

  function FocusedData() {
    return (
      <div style={{overflow: "hidden", display: "flex"}}>
        <div style={{width: "50%"}}>
          <div style={focusedDataTitleLeft}>Current Profile</div>
          <div style={{
              backgroundColor: "white", 
              border: "1px solid black",
              borderRight: "0px",
              marginLeft: "8px",
              height: "385px"}}>
            {getJson()}
          </div>
        </div>
        <div>
          <div style={focusedDataTitleRight}>Old Profile</div>
          <div style={{
              direction:"rtl",
              backgroundColor: "white", 
              border: "1px solid black",
              borderLeft: "0px",
              marginRight: "4px",
              height: "385px",
              textAlign: "right"}}>
            {getJsonReverse()}
          </div>
        </div>
      </div>
    )
  }

  function getJson() {
    return Object.keys(focusedItem.resources[0].data).map((item, i) => (
      <div style={{fontSize: "12px", textIndent:"3px"}}>
        {`${item} | ${focusedItem.resources[0].data[item]}`}
      </div>
    ))
  }

  function getJsonReverse() {
    return Object.keys(focusedItem.resources[0].data).map((item, i) => (
      <div style={{fontSize: "12px", textIndent:"3px"}}>
        {`${focusedItem.resources[0].data[item]} | ${item}`}
      </div>
    ))
  }

  // console.log(JSON.stringify(focusedItem.resources[0].data))

  if (focusedItem.resources[0].data.associatedDevices) {
    return (
      <div style={style.focusedContainer}>
        <FocusedTitle setFocused={setFocused} focusedItem={focusedItem}/>
        <FocusedData/>
        <FocusedHistory focusedItem={focusedItem}/>
      </div>
    )
  }

  // return (
  //   <div style={{margin: "36px 0px 0px 0px"}}>
  //     <div style={style.focusedItemTitleContainer}>
  //       <div style={{width: "25%"}}></div>  
  //       {/* <div style={style.focusedItemTitle}>{focusedItem[0].resources[0].data[[1]]}</div> */}
  //       {/* <div onClick={() => {props.setTempItem(item)}} style={style.focusClose}>+</div>
  //       <div onClick={() => {props.setTempItem(item); handleFocusChange(item)}} style={style.focusClose}>+</div> */}
  //     </div>
  //     <div style={style.focusedItemDD}>
  //       {/* <AssociatedItem profile={item} title={item[3]}/>
  //       <ProfileHistory profile={[item[0].resources[0].data[item[1]], item[2]]}/> */}
  //     </div>
  //   </div>
  // )
}
