import {React, useState} from 'react';
import * as style from "../css/searchList";
import ListItem from "./listItem";
import IndexMenu from "./indexMenu";
import getDeviceData from "./getDeviceData";

let string = "";
let searchData = getDeviceData;
let relevantSearch = "mac";
let filteredSearch = [];

//Removes duplicates and returns a list of devices that include any data value corresponding with user input string.
const returnSearch = () => {
  let infoType = [];
  let unfilteredSearch = [];

  searchData.forEach(profile => {
    profile.forEach(e => {
      if (!e.data) e.data = "";
      if ((e.data).toString().toLowerCase().includes(string)) {
        infoType.push(e.type);
        unfilteredSearch.push(profile);
      }
    });
  });
  filteredSearch = [...new Set(unfilteredSearch)];

  //Highlights most relavent searchindex.
  let counts = [];
  infoType.forEach(e => {
    counts[e] = (counts[e] || 0) + 1;
  });
  relevantSearch = Object.keys(counts)[0];
  infoType = [];
};

export default function SearchList() {
  const [device, setDevice] = useState(searchData);
  const [searchTypeStyle, setSearchTypeStyle] = useState(style.searchTypeStyle)
  const [searchTypeLeftStyle, setSearchTypeLeftStyle] = useState(style.searchTypeLeftStyle)
  
  const handleInputChange = e => {
    string = e.target.value;
    returnSearch();
    setDevice(filteredSearch);
  }

  const handleSearchTypeChange = e => {
    console.log(searchTypeLeftStyle === e.target.style)
    console.log(searchTypeStyle === e.target.style)

    if (searchTypeStyle === style.searchTypeStyle) {
      setSearchTypeStyle(style.searchTypeStyleHighlight)
    }
    else {
      setSearchTypeStyle(style.searchTypeStyle)
    }
    // searchTypeStyle === style.searchTypeStyle ? setSearchTypeStyle(style.searchTypeStyleHighlight) : setSearchTypeStyle(style.searchTypeStyle)
  }

  //Renders a list with all devices returned to filteredSearch and passed to device state.
  const renderPhoneList = () => {
    return device.map((item, i) => (
      <ListItem 
        key={`ID${i}`}
        mac={device[i][0].data}
        user={device[i][1].data.toLowerCase()}
        line={device[i][2].data}
        mask={device[i][3].data}
        ip={device[i][4].data}
        location={device[i][5].data.toLowerCase()}
      />
    ));
  };

  return (
    <div>
      <div style={{display: "flex"}}>
        <div onClick={e => handleSearchTypeChange(e)} style={searchTypeLeftStyle}>Device</div>
        <div onClick={e => handleSearchTypeChange(e)} style={searchTypeStyle}>User</div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <IndexMenu relevantSearch={relevantSearch}/>
      <div style={style.listContainerStyle}>{renderPhoneList()}</div>
    </div>
  );
};
