import * as style from "../../css/light/focusedList";
// import AssociatedItem from '../searchList/associatedItem';
import FocusedTitle from "./focusedTitle"
import FocusedHistory from "./focusedHistory";
import { useRef } from "react";

let focusedDataTitle = {
  backgroundColor: "#4382BC",
  textAlign: "center",
}

let focusedDataInfo = {
  width: "50%"
}

export default function FocusedList({focused, setFocused}) {
  if (!focused[0]) return <div></div>
  let focusedItem = require(`../../data/${focused[1]}/${focused[0]}.json`)

  function ProfileJSON() {
    const dataRef = useRef(null)

    getJson().forEach((item, i) => {
      console.log(item)
    })

    return getJson().map((item, i) => (
      <div 
          ref={dataRef} 
          onMouseEnter={e => {
            e.currentTarget.style.backgroundColor = "#b7eef5"
          }}
          onMouseLeave={e => {
            e.currentTarget.style.backgroundColor = "white"
          }}
          style={{display: "flex", backgroundColor: "white"}}
      >
        <div style={focusedDataInfo}>
          {item}
        </div>
        <div style={{width: "50%", direction: "rtl"}}>
          {focusedItem.resources[0].data[item]}
        </div>
      </div>
    ))
  }

  function FocusedData() {
    return (
      <div style={{overflow: "hidden", display: "flex"}}>
        <div style={{minWidth: "97%", margin: "12px auto 0px auto", border: "1px solid black", height: "398px", backgroundColor: "white"}}>
          <div style={focusedDataTitle}>Current Profile</div>
          <ProfileJSON/>
        </div>  
      </div>
    )
  }

  function getJson() {
    return Object.keys(focusedItem.resources[0].data).map((item, i) => (
      <div style={{fontSize: "11px", textIndent:"3px"}}>
        {item}
      </div>
    ))
  }

  if (focusedItem.resources[0].data.associatedDevices) {
    return (
      <div style={style.focusedContainer}>
        <FocusedTitle setFocused={setFocused} focusedItem={focusedItem}/>
        <FocusedData/>
        <FocusedHistory focusedItem={focusedItem}/>
      </div>
    )
  }
}
