import { useState } from 'react'
import Alerts from './alerts/alerts'
import NavMenu from './navigation/navMenu'
import NavButton from './navigation/navButton'
import Settings from './settings/settings'
import * as style  from './style'

let navButtonOpen = {
    transition: 'all 0.3s',
    opacity: 1,
    visibility: 'visible'
}

let navButtonClose = {
    transition: 'all 0.3s',
    opacity: 0,
    visibility: 'hidden'
}

let alertContainer = {
    width: '90%',
    height: '93%',
    margin: '10px auto 0 auto',
    border: '1px solid #022129',
    borderRadius: 5,
    backgroundColor: '#e4f7ff'
}

let alertOpen = {
    transition: 'all 0.3s',
    opacity: 1,
    visibility: 'visible'
}

let alertClose = {
    transition: 'all 0.3s',
    opacity: 0,
    visibility: 'hidden'
}

export default function Options() {
    const [ddlContainer, setDdlContainer] = useState({...style.ddlContainer, ...style.ddlClose})
    const [ddlIndicator, setDdlIndicator] = useState(style.ddlIndicator)
    const [navContent, setNavContent] = useState(style.navContent)
    const [alertContent, setAlertContent] = useState({...alertContainer, ...alertClose})
    const [currentId, setCurrentId] = useState('nav')
    const [status, setStatus] = useState(false)

    function handleClick(id, pos) {
        if (status && id === currentId) {
            setDdlContainer({...ddlContainer, ...style.ddlClose})
            setNavContent({...navContent, opacity: 0})
            setAlertContent({...alertContent, ...alertClose})
            setDdlIndicator({...ddlIndicator, opacity: 0})
            setStatus(false)
        } else {
            setDdlContainer({...ddlContainer, ...style.ddlOpen})
            setNavContent({...navContent, opacity: 1})
            setAlertContent({...alertContent, ...alertOpen})
            setDdlIndicator({...ddlIndicator, opacity: 1, left: 11 + (101*pos)})
            setCurrentId(id)
            setStatus(true)
        }
    }

    function handleMouseLeave() {
        setDdlContainer({...ddlContainer, ...style.ddlClose})
        setNavContent({...navContent, opacity: 0})
        setAlertContent({...alertContent, ...alertClose})
        setDdlIndicator({...ddlIndicator, opacity: 0})
        setStatus(false)
    }

    function renderDdlContent() {
        switch(currentId) {
            case 'nav':
                return (
                    <span style={navContent}>
                        <div style={{height: 10}}/>
                        <NavButton uri='/ld-tickets' content='ISD Tickets'/>
                    </span>
                )

            case 'alert':
                let alerts = []

                let style = {
                    width: 165,
                    height: '100%',
                    margin: '170px auto 0 auto',
                    opacity: 0.4
                }

                if (alerts.length < 1) {
                    return (
                        <div style={alertContent}>
                            <div style={style}>There are no new alerts</div>
                        </div>
                    )
                }

                else return alerts.map((item, i) => {
                    <div>

                    </div>
                })

            case 'setting':
                return (
                    <div>This is SETTING</div>
                )

            default: return <div></div>
        }


    }
    
    return (
        <span onMouseLeave={handleMouseLeave} style={style.container}>
            <span style={style.menuContainer}>
                <div onClick={() => handleClick('nav', 0)}><NavMenu/></div>
                <div onClick={() => handleClick('alert', 1)}><Alerts/></div>
                <div onClick={() => handleClick('setting', 2)}><Settings/></div>
            </span>

            <span style={ddlContainer}>
                <div style={ddlIndicator}></div>
                {renderDdlContent()}
            </span>
        </span>
    )
}
