import returnSearch from "./returnSearch"
import getDeviceData from "../dataRetrieval/getDeviceData";
import getUserData from "../dataRetrieval/getUserData";

export default function getSearch(string, filteredSearch, listToRender) {
  let temp = {}

  listToRender.forEach(e => {
    switch(e) {
      case "Device":
        temp[e.toLowerCase()] = returnSearch(getDeviceData, string.target.value.toLowerCase(), filteredSearch.device.results); 
        break
      case "User": 
        temp[e.toLowerCase()] = returnSearch(getUserData, string.target.value.toLowerCase(), filteredSearch.user.results); 
        break
      case "Did": 
        temp[e.toLowerCase()] = returnSearch(getDidData, string.target.value.toLowerCase(), filteredSearch.did.results); 
        break
      default: break
    } 

  })
  return temp
}
