import React, {createRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total
  const [highlighted, setHighlighted] = useState(1)

  const handleClick = (e) => {
    console.log(e.target.id)
  }

  let pageNumber = {
    marginRight: "10px", 
    cursor: "pointer", 
    marginTop: "8px", 
    color: "#4382BC"
  }

  const renderPageList = () => {
    let visible = [2, 3, 4, total.device]
    let pages = []

    for (let i in visible) {
      if (parseInt(i)+1 === visible.length) {
        pages.push(<div key={`Page${visible[i]}`} id={visible[i]} onClick={(e) => handleClick(e)} style={pageNumber}>{`...${visible[i]}`}</div>)
      }
      else {
        pages.push(<div key={`Page${visible[i]}`} id={visible[i]} onClick={(e) => handleClick(e)} style={pageNumber}>{visible[i]}</div>)
      }
    }
    return (
      pages.map((i, item) => {
        console.log(i.ref)
        return i
      })
    )
  }


  return (
    <div style={{display: "flex", overflow: "hidden "}}>  
      <div style={style.leftArrow}/>
        <div style={{marginRight: "10px", cursor: "pointer", marginTop: "8px"}}>1</div>
        <div style={{display: "flex"}}>
          {renderPageList()}
        </div>
      <div id="test" test="test" onClick={e => handleClick(e)} style={style.rightArrow}/>
    </div>
  )
}
