import * as style from "../../css/searchList";

export default function handleSearchTypeChange(targ, data, ret=['Device', 'User', 'DID', 'borderWidth']) {

  // data['case'], ['true'], ['false']
  
  let results = {}
  data = data['default']['display']
  
  ret.forEach((type) => {
    if (targ.target.textContent === `${type}`) {
      let bool = !targ.target.style['borderWidth'] ? data['case'][type.toLowerCase()] : data['case']['borderWidth']
      bool.forEach((value, i) => {
        console.log(Object.keys(data[Boolean(value)])[i])
        data[Object.keys(data[Boolean(value)])[i]]['highlighted'] = Boolean(value)
        data[Object.keys(data[Boolean(value)])[i]]['style'] = style[data[Boolean(value)][Object.keys(data[Boolean(value)])[i]]]
        // 'displayStyle': display[Boolean(value)][Object.keys(display[Boolean(value)])[i]]}
      })
    }
  })

  // console.log(data)

  return results
}
