import React, {useCallback} from "react"
import * as style from "../../css/light/totalBar"

export default function Total({setPageNumber, profile, listToRender}) {

  const onKeyPress = useCallback(e => {
    if (e.key === "Enter") setPageNumber(tempItem)
  }, [setPageNumber, tempItem])

  const renderTotalItems = () => {
    return listToRender?.map((item, i) => (
      <div style={style.totalBarItem}>{`${item}: ${profile[item.toLowerCase()]?.length}`}</div>
    ))
  }

  const renderItemsPerPage = () => {
    return (
      <div>
        <div style={style.totalTitle2}>Items/Page</div>
        <input placeholder="10" style={style.totalInput} 
          // onChange={useCallback(e =>setTempItem(parseInt(e.target.value)), [setTempItem])}
          onKeyPress={onKeyPress}
        />
        <div 
            style={style.totalButton}
            // onClick={useCallback(() => setPageNumber(tempItem), [tempItem, setPageNumber])}
            >
          <div style={style.totalButtonLine}></div>
        </div>
      </div>
    )
  }

  return (
    <div style={style.totalContainer}>
      <div style={style.totalTitle1}>Total Count</div>
      {renderTotalItems()}
      {renderItemsPerPage()}
    </div>
  )

 

