import * as style from "./style/focusedStyle";
import FocusedTitle from "./focusedTitle"
import FocusedHistory from "./focusedHistory";
import FocusedProfile from "./focusedProfile"

//Extends Main
export default function Focused({focused, setFocused, locationHierarchy, rawData}) {
  let focusedItem


  // Disables focused for LAS and DID items as they have no individual profiles //
  if (!focused[0] || focused[1] === "las" || focused[1] === "did") return <div></div>


  // Retrieves JSON for item in rawData if matches currently focused profile //
  for (let i in rawData[focused[1]]) {
    if (focused[0] === rawData[focused[1]][i]['profile']) {
      focusedItem = rawData[focused[1]][i]
      break
    }
  }


  return (
    <div style={style.container}>
      <FocusedTitle
        style={style}
        setFocused={setFocused} 
        focusedItem={focusedItem} 
        locationHierarchy={locationHierarchy}/>

      <FocusedProfile
        style={style}
        focusedItem={focusedItem}/>

      <FocusedHistory style={style}/>
    </div>
  )
}
