import React, {useEffect, useRef, useState} from 'react';
import * as style from "../css/light/pageIndex";
import getOverallTotal from './getOverallTotal';

export default function Pages(data, firstNum, lastNum, leftTab, rightTab) {
  let total = getOverallTotal(data)

  const renderPageList = () => {
    let pages = []

    try {
      if (total == 0) {
        firstNum.current.innerHTML = 0
        firstNum.current.style.margin = "8px 2px 0px 0px"
        lastNum.current.style.display = "none"
        rightTab.current.style.display = "none"
        leftTab.current.style.display = "none"
        return
      }
      if (total === 1) {
        firstNum.current.innerHTML = 1
        firstNum.current.style.margin = "8px 2px 0px 0px"
        lastNum.current.style.display = "none"
        rightTab.current.style.display = "none"
        leftTab.current.style.display = "none"
        return
      }
      if (total > 1 && total < 9) {
        firstNum.current.innerHTML = 1
        firstNum.current.style.margin = "8px 8px 0px 0px"
        lastNum.current.style.display = "none"
        rightTab.current.style.display = "none"
        leftTab.current.style.display = "none"
      }
      if (total > 9) {
        firstNum.current.innerHTML = 1
        firstNum.current.style.margin = "8px 8px 0px 0px"
        lastNum.current.style.display = "initial"
        rightTab.current.style.display = "initial"
        lastNum.current.innerHTML = total
      }
    }
    catch {/*First render*/}

    for (let i = 2; i < total; i++) pages.push(i)

    return (
      <div style={{display: "flex"}}>  
        <div style={style.leftArrow}/>
          <div style={{display: "flex"}}>
            <div ref={firstNum} style={style.firstPage}>1</div>
            <div ref={leftTab} style={style.leftTab}>|</div>
            <div onClick={e => console.log(e.target.innerHTML)} style={{display: "flex", maxWidth: "192px", overflow: "hidden"}}>
              {pages.map((i, item) => (<div key={`Page${i}`} id={i} style={style.pageNumber}>{i}</div>))}
            </div>
            <div ref={rightTab} style={style.rightTab}>|</div>
            <div ref={lastNum} style={style.lastPage}>{total}</div>
          </div>
        <div style={style.rightArrow}/>
      </div>
    )
  }
}
