import {useRef, React, useState} from 'react';
import * as style from "../css/searchList";
import ListDeviceItem from "./listDeviceItem";
import ListUserItem from "./listUserItem";
import ListDidItem from "./listDidItem";
import IndexMenu from "./indexMenu";
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";
import getDidData from "./getDidData";
import Pages from "./pages";

let string = "";
let searchDevice = getDeviceData;
let searchUser = getUserData;
let searchDid = getDidData;
let deviceRelevantSearch = "mac";
let userRelevantSearch = "id"
let didRelevantSearch = "did"
let filteredDeviceSearch = [""];
let filteredUserSearch = [""];
let filteredDidSearch = [""];
let phoneContainer = [];

export default function SearchList() {
  const [device, setDevice] = useState(searchDevice);
  const [user, setUser] = useState(searchUser);
  const [did, setDid] = useState(searchDid);
  const [deviceTotal, setDeviceTotal] = useState(Math.ceil(searchDevice.length/ 100))
  const [userTotal, setUserTotal] = useState(Math.ceil(searchUser.length/ 100))
  const [DidTotal, setDidTotal] = useState(Math.ceil(searchDid.length/ 100))
  const [scroll, setScroll] = useState({scrollTop: 0});
  const [deviceDisplay, setDeviceDisplay] = useState(true);
  const [userDisplay, setUserDisplay] = useState(true);
  const [didDisplay, setDidDisplay] = useState(true);
  const [searchTypeDeviceStyle, setSearchTypeDeviceStyle] = useState(style.searchTypeDeviceStyle);
  const [searchTypeUserStyle, setSearchTypeUserStyle] = useState(style.searchTypeUserStyle);
  const [searchTypeDidStyle, setSearchTypeDidStyle] = useState(style.searchTypeDidStyle);
  const deviceIndexRef = useRef(null);
  const userIndexRef = useRef(null);
  const scrollRef = useRef({scrollTop: 0});
  
  //Removes duplicates and returns a list of devices that include any data value corresponding with user input string.
  const returnSearch = () => {
    let deviceInfoType = [];
    let userInfoType = [];
    let didInfoType = [];
    let unfilteredDeviceSearch = [];
    let unfilteredUserSearch = [];
    let unfilteredDidSearch = [];
  
    searchDevice.forEach(profile => {
      profile.forEach(e => {
        if (e.data.toString().toLowerCase().includes(string)) {
          deviceInfoType.push(e.type);
          unfilteredDeviceSearch.push(profile);
        }
      });
    });
    filteredDeviceSearch = [...new Set(unfilteredDeviceSearch)];
  
    searchUser.forEach(profile => {
      profile.forEach(e => {
        if (e.data.toString().toLowerCase().includes(string)) {
          userInfoType.push(e.type);
          unfilteredUserSearch.push(profile);
        }
      })
    })
    filteredUserSearch = [...new Set(unfilteredUserSearch)];

    searchDid.forEach(profile => {
      profile.forEach(e => {
        if (e.data.toString().toLowerCase().includes(string)) {
          didInfoType.push(e.type);
          unfilteredDidSearch.push(profile);
        }
      })
    })
    filteredDidSearch = [...new Set(unfilteredDidSearch)];
  
    //Highlights most relavent searchindex.
    let deviceCounts = [];
    deviceInfoType.forEach(e => {
      deviceCounts[e] = (deviceCounts[e] || 0) + 1;
    });
    deviceRelevantSearch = Object.keys(deviceCounts)[0];
    deviceInfoType = [];

    let userCounts = [];
    userInfoType.forEach(e => {
      userCounts[e] = (userCounts[e] || 0) + 1;
    });
    userRelevantSearch = Object.keys(userCounts)[0];
    userInfoType = [];

    let didCounts = [];
    didInfoType.forEach(e => {
      didCounts[e] = (didCounts[e] || 0) + 1;
    });
    didRelevantSearch = Object.keys(didCounts)[0];
    didInfoType = [];
  };

  const handleInputChange = e => {
    string = e.target.value.toLowerCase();
    returnSearch();
    setDevice(filteredDeviceSearch);
    setUser(filteredUserSearch);
    setDid(filteredDidSearch)
  }

  const handleSearchTypeChange = e => {
    if (e.target.textContent === "Device" && !e.target.style["borderWidth"]) {
      setSearchTypeDeviceStyle(style.searchTypeDeviceStyleHighlight)
      setSearchTypeUserStyle(style.searchTypeUserStyle)
      setSearchTypeDidStyle(style.searchTypeDidStyle)
      setDeviceDisplay(true)
      setUserDisplay(false)
      setDidDisplay(false)
      return
    }

    if (e.target.textContent === "User" && !e.target.style["borderWidth"]) {  
      setSearchTypeUserStyle(style.searchTypeUserStyleHighlight)
      setSearchTypeDeviceStyle(style.searchTypeDeviceStyle)
      setSearchTypeDidStyle(style.searchTypeDidStyle)
      setDeviceDisplay(false)
      setUserDisplay(true)
      setDidDisplay(false)
      return
    }

    if (e.target.textContent === "DID" && !e.target.style["borderWidth"]) {  
      setSearchTypeUserStyle(style.searchTypeUserStyle)
      setSearchTypeDeviceStyle(style.searchTypeDeviceStyle)
      setSearchTypeDidStyle(style.searchTypeDidStyleHighlight)
      setDeviceDisplay(false)
      setUserDisplay(false)
      setDidDisplay(true)
      return
    }

    if (e.target.style["borderWidth"]) {
      setSearchTypeDeviceStyle(style.searchTypeDeviceStyle)
      setSearchTypeUserStyle(style.searchTypeUserStyle)
      setSearchTypeDidStyle(style.searchTypeDidStyle)
      setDeviceDisplay(true)
      setUserDisplay(true)
      setDidDisplay(true)
      return
    }
  }

  const handleScroll = () => {
    setScroll({scrollTop: scrollRef.current.scrollTop})
    // if (scroll.scrollTop-28 > phoneContainer.length*35) {
    //   userIndexRef.current.style.position = "fixed"
    //   userIndexRef.current.style.top = "50px"
    //   return
    // }
    // userIndexRef.current.style.position = "static"
  }

  //Renders a list with all devices returned to filteredSearch and passed to device state.
  const renderPhoneList = () => {
    phoneContainer = []
    let i = 0
    while (i < 100) {
      if (!device[i]) break
      phoneContainer.push(device[i])
      i++
    }
    
    if (deviceDisplay) {
      return phoneContainer.map((item, i) => (
        <ListDeviceItem
          key={`ID${i}D`}
          mac={item[0].data}
          user={item[1].data}
          line={item[2].data}
          mask={item[3].data}
          ip={item[4].data}
          location={item[5].data}
        />
      ));
    }
  };

  const renderUserList = () => {
    let container = []
    let i = 0
    while (i < 100) {
      if (!user[i]) break
      container.push(user[i])
      i++;
    }
    if (userDisplay) {
      return container.map((item, i) => (
        <ListUserItem
          key={`ID${i}U`}
          id={item[0].data}
          name={item[1].data}
          primary={item[2].data}
          department={item[3].data}
          device={item[4].data}
          location={item[5].data}
        />
      ));
    }
  };

  const renderDidList = () => {
    let container = []
    let i = 0
    while (i < 100) {
      if (!did[i]) break
      container.push(did[i])
      i++;
    }
    if (didDisplay) {
      return container.map((item, i) => (
        <ListDidItem
        key={`ID${i}DID`}
        did={item[0].data}
        line={item[1].data}
        location={item[2].data}
        area={item[3].data}
        description={item[4].data}
        assigned={item[5].data}
        />
      ))
    }
  }

  const renderDeviceIndexMenu = () => {
    if (deviceDisplay) {
      if (filteredDeviceSearch.length === 0) return <div></div>
      return <IndexMenu
        relevantSearch={deviceRelevantSearch} 
        index1={["MAC Address", "mac"]} 
        index2={["User", "user"]} 
        index3={["Primary Line", "line"]} 
        index4={["Line Mask", "mask"]} 
        index5={["IP Address", "ip"]}
        index6={["Location", "location"]}
      />
    }
  }

  const renderUserIndexMenu = () => {
    if (userDisplay) {
      if (filteredUserSearch.length === 0) return <div></div>
      return <IndexMenu
        relevantSearch={userRelevantSearch} 
        index1={["User ID", "id"]} 
        index2={["Name", "name"]}
        index3={["Primary Line", "primary"]} 
        index4={["Department", "department"]}
        index5={["Device", "device"]}
        index6={["Location", "location"]}
      />
    }
  }

  const renderDidIndexMenu = () => {
    if (didDisplay) {
      if (filteredDidSearch.length === 0) return <div></div>
      return <IndexMenu
        relevantSearch={didRelevantSearch}
        index1={["DID", "did"]} 
        index2={["Associated Line", "line"]} 
        index3={["Location", "location"]}
        index4={["Area", "area"]}
        index5={["Description", "description"]}
        index6={["Assigned", "assigned"]} 
      />
    }
  }

  // const renderPageSelect = () => {
    
  // }

  return (
    <div style={{minWidth: "59.5vw"}}>
      <div style={{display: "flex", minWidth: "59.5vw"}}>
        <div style={{display: "flex"}}>
          <div onClick={e => handleSearchTypeChange(e)} style={searchTypeDeviceStyle}>Device</div>
          <div onClick={e => handleSearchTypeChange(e)} style={searchTypeUserStyle}>User</div>
          <div onClick={e => handleSearchTypeChange(e)} style={searchTypeDidStyle}>DID</div>
        </div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <div >
        <div ref={scrollRef} onScroll={() => handleScroll()} style={style.listContainerStyle}>
          <div ref={deviceIndexRef}>
            {/* <div style={{width: "58.9vw", height: "3px", margin: "1px 0px -12px 0px", backgroundColor: "#4382BC"}}></div> */}
            {renderDeviceIndexMenu()}
          </div>
          {renderPhoneList()}
          <div ref={userIndexRef}>
            {/* <div style={{width: "58.9vw", height: "3px", margin: "2px 0px -12px 0px", backgroundColor: "#4382BC"}}></div> */}
            {renderUserIndexMenu()}
          </div>
          {renderUserList()}
          {/* <div style={{width: "58.9vw", height: "3px", margin: "1px 0px -12px 0px", backgroundColor: "#4382BC"}}></div> */}
          <div>
            {renderDidIndexMenu()}
          </div>
          {renderDidList()}
        </div>
        <Pages pageTotal={deviceTotal, userTotal, DidTotal}/>
      </div>
    </div>
  );
};
