import * as style from "../../css/searchList";

export default function handleSearchTypeChange(targ, data, ret=['Device', 'User', 'DID', 'borderWidth']) {

  const display = {
    'case': {
      'device': [1,0,0],
      'user': [0,1,0],
      'did': [0,0,1],
      'borderWidth': [0,0,0]
    },
    "true": {
      'device': "searchTypeDeviceStyleHighlight",
      'user': "searchTypeUserStyleHighlight",
      'did': "searchTypeDidStyleHighlight"
    },
    'false': {
      'device': "searchTypeDeviceStyle",
      'user': "searchTypeUserStyle",
      'did': "searchTypeDidStyle"
    }
  }
  
  let results = {}
  
  ret.forEach((type) => {
    if (targ.target.textContent === `${type}`) {
      let bool = !targ.target.style['borderWidth'] ? display['case'][type.toLowerCase()] : display['case']['borderWidth']
      bool.forEach((value, i) => {
        results[Object.keys(display[Boolean(value)])[i]] = {'display': Boolean(value), 'displayStyle': display[Boolean(value)][Object.keys(display[Boolean(value)])[i]]}
      })
    }
  })

  return results
}
