import users from "../../data/user_list.json"
import profiles from "../../data/allstream_profile_list.json"

for (let i in profiles) {
    console.log(Object.key(profiles[i]))
}

const getUserData = () => {
  let i = 0;
  let searchUser = [];

  users['users'].forEach(user => {
    const container = require(`../../data/users/${user}.json`);
    const changeName = container => !container ? "" : container
    const changePrim = container => !container ? "" : container.pattern
    const changePhone = container => !container ? "" : container.name

    searchUser.push([
      {
        key: i,
        type: "id", 
        data: changeName(container.resources[0].data.userid)
      },
      {
        key: i,
        type: "name",
        data: changeName(container.resources[0].data.displayName)
      },
      {
        key: i,
        type: "primary",
        data: changePrim(container.resources[0].data.primaryExtension)
      },
      {
        key: i,
        type: "department",
        data: changeName(container.resources[0].data.title)
      },
      {
        key: i,
        type: "device",
        data: changePhone(container.resources[0].data.Phone[0])
      },
      {
        key: i,
        type: "location",
        data: container.resources[0].meta.hierarchy.split("_").pop().toLowerCase()
      }
    ])
  })

  return searchUser
}

export default getUserData();
