import React, {createRef, useState} from 'react';
import * as style from "../css/pageIndex";

export default function Pages(props) {
  let total = props.total
  const [highlighted, setHighlighted] = useState(1)

  const handleClick = (e, data) => {
    console.log(e.target.text)
  }

  const renderPageList = () => {
    let visible = [2, 3, 4, `...${total.device}`]
    let pages = []

    for (let i in visible) {
      pages.push(<div key={`Page${i}`} ref={createRef(null)} onClick={(e) => handleClick(e)} style={{marginRight: "10px", cursor: "pointer", marginTop: "8px", color: "#4382BC"}}>{visible[i]}</div>)
    }
    return pages
  }


  return (
    <div style={{display: "flex", overflow: "hidden "}}>
      <div style={style.leftArrow}/>
        <div style={{marginRight: "10px", cursor: "pointer", marginTop: "8px"}}>1</div>
        <div style={{display: "flex"}}>
          {renderPageList()}
        </div>
      <div style={style.rightArrow}/>
    </div>
  )
}
