import { useEffect } from 'react';


export default function Loading({loading, loadingText, setLoadingText}) {
  if (!loading) return <div></div>

  useEffect(() => {
    loadingAnimation()
  }, [])

  const loadingAnimation = () => {
    let string = "Loading"
    let value = 0

    setInterval(() => {
      if (value >= 3) {
        value = 0
        string = "Loading"
      }

      else string = string + "."
      value++

      setLoadingText(string)
    }, 1200)
  }

  return (
    <div style={loading}>
      {loadingText}
    </div>
  )
}
