import hierarchy from "../../data/location_hierarchy.json"

export default function getLocationHierarchy(phone) {
  let link = ""
  let store = ""
  try {
    let file = require(`../../data/phones/${phone}.json`)
    let pkid = file.resources[0].meta.pkid

    if (file.resources[0].meta.hierarchy.includes("Head Office")) store = "head_office"
    else store = "store_" + file.resources[0].meta.hierarchy.split("Store ")[1]
    
    let storeId = hierarchy.stores[store]
    link = (
      `https://hcsportal.allstream.com/#/form/?id=1632343452137&hierarchy=${storeId}` +
      `&target=%2Fapi%2Frelation%2FSubscriberPhone%2F${pkid}` +
      `%2F%3Fpolicy_name%3DSubscriberPhoneMenuItemCustomer%26template_name%3DSubscriberPhonePrePopulate`
      )
    }
  catch {/*pass*/}

  return link
}
