import { useEffect, useState } from "react"
import callTickets from "./api/callTickets"

let itemContainer = {
    height: 50, 
    width: 600, 
    backgroundColor: '#063b50', 
    margin: '5px 0 0 10px',
    borderRadius: 5
}

let isd = {
    lineHeight: 2.3,
    marginLeft: 10,
    color: '#e4f7ff',
    fontSize: 22,
    // fontWeight: 'bold'
}

let status = {
    lineHeight: 2.3,
    marginLeft: 10,
    color: '#e4f7ff',
    fontSize: 22,
    textTransform: 'toTitleCase'
}

function Ticket({data}) {
    function renderItems() {
        return Object.keys(data.items).map((item, i) => (
            <div key={`Ti${i}`}>{item} {data.items[item]}</div>
        ))
    }

    return (
        <div style={itemContainer}>
            <div style={{display: 'flex', justifyContent: 'space-between'}}>
                <div style={isd}>{data.isd}</div>
                <div style={status}>{data.status.title( )}</div>
            </div>
            {/* <div>Status: {data.status}</div>
            <div>items: {renderItems()}</div> */}
        </div>
    )
}

export default function LdTickets() {
    const [tickets, setTickets] = useState([]) 

    useEffect(() => {
        const getData = async () => {
            let res = await callTickets()
            setTickets(res.sort((a, b) => b.isd - a.isd))
        }

        getData()
    }, [])

    function renderTickets() {
        return tickets.map((item, i) => (
            <div key={`T${i}`}>
                <Ticket data={item}/>
            </div>
        ))
    }

    return (
        <div>{renderTickets()}</div>
    )
}
