import users from "../../data/user_list.json"
import profiles from "../../data/allstream_profile_list.json"

export default function getUserData() {
  let searchUser = [];

  const validate = (data) => {
    try {return data ? data : ""}
    catch {return ""}
  }
  
  users['users'].forEach((user, i) => {
    const data = require(`../../data/${profiles['users']}/${user}.json`);

    searchUser.push([
      {
        key: i,
        type: "id", 
        data: validate(data.resources[0].data.userid)
      },
      {
        key: i,
        type: "name",
        data: validate(data.resources[0].data.displayName)
      },
      {
        key: i,
        type: "primary",
        data: validate(data.resources[0].data.primaryExtension)
      },
      {
        key: i,
        type: "department",
        data: validate(data.resources[0].data.title)
      },
      {
        key: i,
        type: "device",
        data: validate(data.resources[0].data.Phone[0])
      },
      {
        key: i,
        type: "location",
        data: validate(data.resources[0].meta.hierarchy.split("_").pop().toLowerCase())
      }
    ])
  })
  console.log(searchUser)
  return searchUser
}

getUserData()
