export default function Loading({loading, loadingText, setLoadingText}) {
  if (!loading) return <div></div> 

  const loadingAnimation = () => {
    let value = 0

    setTimeout(() => {
      if (value === 3) {
        value = 0
        setLoadingText("Loading")
      }

      for (let i = 0; i < value; i++) {
        setLoadingText(loadingText + ".")
      }

      console.log(loadingText)

      return loadingText
    }, 300)
  }

  return (
    <div style={loading}>
      {loadingAnimation()}
    </div>
  )
}
