export default function rotateArrow(isDown, setIsDown, arrowRef) {
  if (!isDown) {
    function rotateRight() {
      let rot = 45

      if (rot < 135) {
        arrowRef.current.style.transform = `rotate(${rot}deg)`
        rot+=3
      }
    }
    let interval = setInterval(rotateRight, 1)
    if (rot === 135) clearInterval(interval)
    setIsDown(true)
  }

  else {
    function rotateLeft() {
      let rot = 135

      if (rot > 45) {
        arrowRef.current.style.transform = `rotate(${rot}deg)`
        rot-=3
      }
    }
    let interval = setInterval(rotateLeft, 1)
    if (rot === 45) clearInterval(interval)
    setIsDown(false)
  }
}
