import { useState } from 'react'
import Alerts from './alerts/alerts'
import NavMenu from './navigation/navMenu'
import NavButton from './navigation/navButton'
import Settings from './settings/settings'

let optionsContainer = {
    zIndex: 5,
    display: 'flex',
    width: '25%',
    height: 30,
    margin: "10px 0 0 auto",
    flexWrap: 'wrap',
    userSelect: "none",
}

let optionsMenuContainer = {
    display: 'flex',
    width: 304,
    marginLeft: 'auto'
}

let optionsDdlContainer = {
    width: 304,
    height: 400,
    marginLeft: 'auto',
    backgroundColor: "#063b50",
    borderRadius: '0 0 5px 5px',
}

let optionsDdlIndicator = {
    position: 'relative',
    top: 3,
    width: 80,
    height: 1,
    backgroundColor: 'white',
    opacity: 0,
    transition: 'opacity 0.5s, left 0.3s'
}

let navButtonStyle = {
    display: 'block',
    width: 'calc(90% - 5px)',
    height: 30,
    borderRadius: 5,
    margin: '5px auto 0 auto',
    padding: '7px 0 0 5px',
    cursor: 'pointer',
    textDecoration: 'none',
    color: 'white',
    opacity: 0
}

let navButtonOpen = {
    transition: 'all 0.3s',
    opacity: 1,
    visibility: 'visible'
}

let navButtonClose = {
    transition: 'all 0.3s',
    opacity: 0,
    visibility: 'hidden'
}

let navContainer = {
    width: '90%',
    height: '93%',
    margin: '10px auto 0 auto'
}

let alertContainer = {
    width: '90%',
    height: '93%',
    margin: '10px auto 0 auto',
    border: '1px solid #022129',
    borderRadius: 5,
    backgroundColor: '#e4f7ff'
}

let alertOpen = {
    transition: 'all 0.3s',
    opacity: 1,
    visibility: 'visible'
}

let alertClose = {
    transition: 'all 0.3s',
    opacity: 0,
    visibility: 'hidden'
}

let ddlOpen = {
    transition: 'all 0.5s',
    maxHeight: 400,
    visibility: 'visible'
}

let ddlClose = {
    transition: 'all 0.5s',
    maxHeight: 0,
    visibility: 'hidden'
}

export default function Options() {
    const [ddlContainer, setDdlContainer] = useState({...optionsDdlContainer, ...ddlClose})
    const [ddlIndicator, setDdlIndicator] = useState(optionsDdlIndicator)
    const [navContent, setNavContent] = useState({...navButtonStyle})
    const [alertContent, setAlertContent] = useState({...alertContainer, ...alertClose})
    const [currentId, setCurrentId] = useState('nav')
    const [status, setStatus] = useState(false)

    function handleClick(id, pos) {
        if (status && id === currentId) {
            setDdlContainer({...ddlContainer, ...ddlClose})
            // setNavContent({...navContent, ...navButtonClose})
            setAlertContent({...alertContent, ...alertClose})
            setDdlIndicator({...ddlIndicator, opacity: 0})
            setStatus(false)
        } else {
            setDdlContainer({...ddlContainer, ...ddlOpen})
            // setNavContent({...navContent, ...navButtonOpen})
            setAlertContent({...alertContent, ...alertOpen})
            setDdlIndicator({...ddlIndicator, opacity: 1, left: 11 + (101*pos)})
            setCurrentId(id)
            setStatus(true)
        }
    }

    function handleMouseLeave() {
        setDdlContainer({...ddlContainer, ...ddlClose})
        // setNavContent({...navContent, ...navButtonClose})
        setAlertContent({...alertContent, ...alertClose})
        setDdlIndicator({...ddlIndicator, opacity: 0})
        setStatus(false)
    }

    function renderDdlContent() {
        switch(currentId) {
            case 'nav':
                return (
                    <span style={navContainer}>
                        <div style={{height: 10}}/>
                        <NavButton style={navContent} uri='/ld-tickets' content='ISD Tickets'/>
                    </span>
                )

            case 'alert':
                let alerts = []

                let style = {
                    width: 165,
                    height: '100%',
                    margin: '170px auto 0 auto',
                    opacity: 0.4
                }

                if (alerts.length < 1) {
                    return (
                        <div style={alertContent}>
                            <div style={style}>There are no new alerts</div>
                        </div>
                    )
                }

                else return alerts.map((item, i) => {
                    <div>

                    </div>
                })

            case 'setting':
                return (
                    <div>This is SETTING</div>
                )

            default: return <div></div>
        }


    }
    
    return (
        <span onMouseLeave={handleMouseLeave} style={optionsContainer}>
            <span style={optionsMenuContainer}>
                <div onClick={() => handleClick('nav', 0)}><NavMenu/></div>
                <div onClick={() => handleClick('alert', 1)}><Alerts/></div>
                <div onClick={() => handleClick('setting', 2)}><Settings/></div>
            </span>

            <span style={ddlContainer}>
                <div style={ddlIndicator}></div>
                {renderDdlContent()}
            </span>
        </span>
    )
}
