import * as style from "../../css/light/focusedList";
import img from "../../images/hcs.png"
import getHcsLink from "../dataRetrieval/getHcsLink";

export default function FocusedTitle({setFocused, focusedItem, locationHierarchy}) {
  return (
    <div style={style.focusedMainTitleContainer}>
      <a href={getHcsLink(focusedItem, locationHierarchy)} target="_blank" rel="noreferrer" style={{width: "100%"}}>
        <img src={img} alt="" style={{height: "80%", cursor: "pointer", float: "left", margin: "auto 0 auto 10px"}}></img>  
      </a>
      <div style={style.focusedMainTitle}>{focusedItem.profile}</div>
      <div style={{display: "flex", width: "100%"}}>
        <div onClick={() => {setFocused([])}} style={style.focusedClose}>+</div>
      </div>
    </div>
  )
}
