import profiles from "../../data/allstream_profile_list.json"
import validateString from "./validateString"

export default function getDeviceData() {
  let profileList = [];

  profiles['devices'].forEach((device, i) => {
    const data = require(`../../data/devices/${device}.json`);

    profileList.push([
      {
        key: i,
        type: "mac",
        data: validateString(data.resources[0].data.name.toUpperCase())
      },
      {
        key: i,
        type: "product",
        data: validateString(data.resources[0].data.product)
      },
      {
        key: i,
        type: "user",
        data: validateString(data.resources[0].data.lines)
      },
      {
        key: i,
        type: "primary",
        data: validateString(data.resources[0].data.lines)
      },
      {
        key: i,
        type: "mask",
        data: validateString(data.resources[0].data.lines)
      },
      {
        key: i,
        type: "ip",
        data: validateString(data.resources[0].data.ip_address)
      },
      {
        key: i,
        type: "location",
        data: validateString(data.resources[0].meta.hierarchy.split("_").pop())
      }
    ]);
  });
  console.log(profileList)
  return profileList;
};

getDeviceData()
