import React, {useEffect, useRef, useState} from 'react';
import * as style from "../css/light/pageIndex";
import getOverallTotal from './getOverallTotal';

export default function Pages(props) {
  const [total, setTotal] = useState(getOverallTotal(props))
  const [pageStyle, setPageStyle] = useState(style.pageNumber)
  const firstNum = useRef(null);
  const lastNum = useRef(null);
  const leftTab = useRef(null);
  const rightTab = useRef(null);

  useEffect(() => {
    setTimeout(() => setTotal(getOverallTotal(props)), 0.01)
  })

  const renderPageList = () => {
    let pages = []
    try {
      if (total === 0) {
        firstNum.current.innerHTML = 0
        lastNum.current.style.visibility = "hidden"
        rightTab.current.style.visibility = "hidden"
        leftTab.current.style.visibility = "hidden"
        return
      }
      if (total === 1) {
        firstNum.current.innerHTML = 1
        lastNum.current.style.visibility = "hidden"
        rightTab.current.style.visibility = "hidden"
        leftTab.current.style.visibility = "hidden"
        return
      }
      if (total < 9) {
        firstNum.current.innerHTML = 1
        lastNum.current.style.visibility = "hidden"
        rightTab.current.style.visibility = "hidden"
        leftTab.current.style.visibility = "hidden"
      }
      else {
        firstNum.current.innerHTML = 1
        lastNum.current.style.visibility = "visible"
        rightTab.current.style.visibility = "visible"
        lastNum.current.innerHTML = total
      }
    }
    catch {/*first render*/}
    for (let i = 2; i < total; i++) pages.push(i)
    return (
      <div style={{display: "flex"}}>
        <div style={style.leftArrow}/>
        <div ref={firstNum} style={style.firstPage}>1</div>
        <div ref={leftTab} style={style.leftTab}>|</div>
        {pages.map((i, item) => (<div key={`Page${i}`} id={i} style={pageStyle}>{i}</div>))}
        <div ref={rightTab} style={style.rightTab}>|</div>
        <div ref={lastNum} style={style.lastPage}>{total}</div>
        <div style={style.rightArrow}/>
      </div>
      )
  }

  return (
    <div style={{display: "flex", maxWidth: "200px"}}>{renderPageList()}</div>
  )
}
