import {React, useEffect, useState} from 'react';
import * as style from "../css/light/main";
import getDeviceData from "./dataRetrieval/getDeviceData";
import getUserData from "./dataRetrieval/getUserData";
import Focused from "./focused/focused"
// import getDidData from "./dataRetrieval/getDidData";
import ProfileList from "./profileList/profileList"
import ProfileButtons from "./profileButtons/profileButtons"
import ProfileTotal from "./profileTotal/profileTotal"
import axios from 'axios';

const getApi = async (listToRender, setProfileData) => {
  let promises = []
  let profileListContainer = {}

  listToRender.forEach(profile => {
    promises.push(axios.get(`http://localhost:5000/${profile}`))
    Promise.all(promises)
      .catch(err => console.log(err))
      .then(async result => {
        console.log(200)
        switch(profile) {
          case "Devices":
            profileListContainer['devices'] = {"devices": await getDeviceData(result[0])}
            break;
          case "Users":
            profileListContainer['users'] = {"users": await getUserData(result[1])}
            break;
          default: return;
        }
      })
      .finally(() => {
        return new Promise((resolve, reject) => {
          resolve(profileListContainer)
          reject(err => console.log(err))
        })
      })
  })  
}

//Extends Main
export default function Main() {
  const [profileData, setProfileData] = useState([])
  const [listToRender, setListToRender] = useState(["Devices", "Users"])
  const [profileDataToRender, setProfileDataToRender] = useState([])
  const [totalToRenderNumber, setTotalToRenderNumber] = useState(50)
  const [focused, setFocused] = useState([])

  

  useEffect(() => {
    const fetchData = async () => {
      const result = await getApi(listToRender)
      console.log(result)
    }
    fetchData()
    // async function getData() {
    //   await getApi(listToRender, (res) => {
    //     console.log(res)
    //   })
    //     // .then(res => console.log(res))
    // }

    // getData()
    
    
  // Removed listToRender dependency so DOM does not re-render every time
  // profile button is clicked
  // eslint-disable-next-line
  }, [fetchData])

  return (
    <div style={style.main}>
      <ProfileButtons
        listToRender={listToRender}
        setListToRender={setListToRender}/>
      <div style={style.containerLeft}>
        <ProfileList 
          focused={focused}
          setFocused={setFocused}
          listToRender={listToRender}
          profileData={profileData}
          profileDataToRender={profileDataToRender}
          setProfileDataToRender={setProfileDataToRender}
          totalToRenderNumber={totalToRenderNumber}/>
        <Focused focused={focused} setFocused={setFocused}/>
      </div>
      <ProfileTotal 
        listToRender={listToRender}
        profileDataToRender={profileDataToRender} 
        totalToRenderNumber={totalToRenderNumber} 
        setTotalToRenderNumber={setTotalToRenderNumber}/>
    </div>
  )
};
