export default function handleSearchTypeChange(targ, data, name, ret=['Device', 'User', 'Line', 'DID']) {
  ret.forEach((type) => {
    let bool = []
    if (targ.target.textContent === `${type}`) {
      if (!targ.target.style['borderWidth']) {
        bool = data['display']['case'][type.toLowerCase()]
        data['display']['nonehighlighted'] = false
      }
      else {
        bool = data['display']['case']['borderWidth']
        data['display']['nonehighlighted'] = true
      }

      try {
        bool.forEach((value, i) => {
          let pointToType = Object.keys(data['display'][Boolean(value)])[i]
          let pointToTarget = data['display'][pointToType]
          let pointToStyle = data['display'][Boolean(value)][Object.keys(data['display'][Boolean(value)])[i]]
  
          pointToTarget['highlighted'] = Boolean(value);
          data['display']['style'][pointToType] = pointToStyle;
        });
      }
      catch {return data}
    };
  });

  return data;
};
