import profiles from "../../data/allstream_profile_list.json"
import validateString from "./validateString";
import getHcsLink from "./getHcsLink";

export default function getUserData() {
  let profileList = [];
  
  profiles['users'].forEach((user, i) => {
    const data = require(`../../data/users/${user}.json`)

    profileList.push([
      {
        key: i,
        type: "id",
        index: "User ID",
        data: function() {
          try {return data.resources[0].data.userid} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "display",
        index: "Display",
        data: function() {
          try {return data.resources[0].data.displayName} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "primary",
        index: "Primary Line",
        data: function() {
          try {return data.resources[0].data.primaryExtension.pattern} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "department",
        index: "Department",
        data: function() {
          try {return data.resources[0].data.title} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "device",
        index: "MAC Address",
        data: function() {
          try {return data.resources[0].data.Phone[0].name} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "location",
        index: "Location",
        data: function() {
          try {return data.resources[0].meta.hierarchy.split("_").pop()} 
          catch {return ""}
        }()
      },
      {
        key: i,
        type: "",
        index: "",
        data: ""
      },
      {
        key: i,
        type: "link",
        index: "Link",
        data: getHcsLink(data.resources[0].data.userid, "users")
      }
    ]);
  });

  return profileList;
};
