let optionsContainer = {
    zIndex: 5,
    display: 'flex',
    width: '25%',
    height: 30,
    margin: "10px 0 0 auto",
    flexWrap: 'wrap',
    userSelect: "none",
}

let optionsMenuContainer = {
    display: 'flex',
    width: 304,
    marginLeft: 'auto'
}

let optionsDdlContainer = {
    width: 304,
    height: 400,
    marginLeft: 'auto',
    backgroundColor: "#063b50",
    borderRadius: '0 0 5px 5px',
}

let ddlOpen = {
    transition: 'all 0.5s',
    maxHeight: 400,
    visibility: 'visible'
}

let ddlClose = {
    transition: 'all 0.5s',
    maxHeight: 0,
    visibility: 'hidden'
}

export {
    optionsContainer,
    optionsMenuContainer,
    optionsDdlContainer,
    ddlOpen,
    ddlClose
}
