export default function getSearch(string, listToRender, profileData) {
  let returnedObject = {}

  let test = listToRender.map(listItem => {
    listItem = listItem.toLowerCase()

    return {
      [listItem]:
        profileData[listItem].filter(list => {
            let bool = list.some(item => {
            let cleanItem   = item['data'].toString().toLowerCase()
            let cleanString = string.target.value.toLowerCase()
    
            if (cleanItem.includes(cleanString)) return true
            else return false
          })
    
          return bool
        })
    }
  })
  
  console.log(test)

  listToRender.forEach((listItem) => {
    let returnedList = []
    listItem = listItem.toLowerCase()

    profileData[listItem]?.forEach(object => {
      for (let i in object) {
        if (object[i]['data'].toString().toLowerCase().includes(string.target.value.toLowerCase())) {
          returnedList.push(object)
          break
        }
      }
    })

    returnedObject[listItem] = returnedList
  })

  console.log(returnedObject)

  return returnedObject
}
