import profiles from "../../data/allstream_profile_list.json"
import validateString from "./validateString"
import getHcsLink from "./getHcsLink";

export default function getDeviceData() {
  let profileList = [];

  profiles['devices'].forEach((device, i) => {
    const data = require(`../../data/devices/${device}.json`);

    profileList.push([
      {
        key: i,
        type: "mac",
        index: "MAC Address",
        data: validateString(value=data.resources[0].data.name.toUpperCase())
      },
      {
        key: i,
        type: "product",
        index: "Product",
        data: validateString(value=data.resources[0].data.product)
      },
      {
        key: i,
        type: "user",
        index: "User ID",
        data: validateString(value=data.resources[0].data.ownerUserName)
      },
      {
        key: i,
        type: "description",
        index: "Description",
        data: validateString(value=data.resources[0].data.description)
      },
      {
        key: i,
        type: "primary",
        index: "Primary Line",
        data: validateString(value=data.resources[0].data.lines.line[0].dirn.pattern)
      },
      {
        key: i,
        type: "mask",
        index: "E164 Mask",
        data: validateString(value=data.resources[0].data.lines.line[0].e164Mask)
      },
      {
        key: i,
        type: "location",
        index: "Location",
        data: validateString(value=data.resources[0].meta.hierarchy.split("_").pop())
      },
      {
        key: i,
        type: "link",
        index: "Link",
        data: getHcsLink(data.resources[0].data.name, "devices")
      }
    ]);
  });
  return profileList;
};

getDeviceData()
