import {useRef, React, useState} from 'react';
import * as style from "../../css/searchList";
import renderIndexMenu from './renderIndexMenu';
import renderDidList from './renderDidList';
import renderUserList from './renderUserList';
import renderDeviceList from './renderDeviceList';
import returnSearch from './returnSearch';
import getDeviceData from "./getDeviceData";
import getUserData from "./getUserData";
import getDidData from "./getDidData";
import Pages from "../pages";

let searchDevice = getDeviceData;
let searchUser = getUserData;
let searchDid = getDidData;
let filteredSearch = {
  "device": {"results": getDeviceData,"relevant": "mac"}, 
  "user": {"results": getUserData,"relevant": "id"},
  "did": {"results": getDidData,"relevant": "did"}
}
let deviceIndex = [["MAC Address", "mac"], ["User", "user"], ["Primary Line", "primary"], ["Line Mask", "mask"], ["Line Mask", "mask"], ["Location", "location"]]
let userIndex = [["User ID", "id"],["Name", "name"], ["Primary Line", "primary"], ["Department", "department"], ["Device", "device"], ["Location", "location"]]
let didIndex = [["DID", "did"], ["Associated Line", "line"], ["Location", "location"], ["Area", "area"], ["Description", "description"], ["Assigned", "assigned"]]

export default function SearchList() {
  const [item, setItem] = useState({
    "device": filteredSearch['device']['results'], 
    "user": filteredSearch['user']['results'], 
    "did": filteredSearch['did']['results']
  })
  const [totalCount, setTotalCount] = useState({
    "device": Math.ceil(filteredSearch.device.results.length/ 100),
    "user": Math.ceil(filteredSearch.user.results.length/ 100),
    "did": Math.ceil(filteredSearch.did.results.length/ 100)
  })
  const [display, setDisplay] = useState({"device": true, "user": true, "did": true})
  const [displayStyle, setDisplayStyle] = useState({"device": style.searchTypeDeviceStyle, "user": style.searchTypeUserStyle, "did": style.searchTypeDidStyle})
  const deviceIndexRef = useRef(null);
  const userIndexRef = useRef(null);
  const scrollRef = useRef({scrollTop: 0});
  
  const handleInputChange = string => {
    let _device = returnSearch(searchDevice, string.target.value.toLowerCase(), filteredSearch.device.results)
    let _user = returnSearch(searchUser, string.target.value.toLowerCase(), filteredSearch.user.results)
    let _did = returnSearch(searchDid, string.target.value.toLowerCase(), filteredSearch.did.results)

    filteredSearch = {
      "device": {"results": _device[0],"relevant": _device[1]}, 
      "user": {"results": _user[0],"relevant": _user[1]},
      "did": {"results": _did[0],"relevant": _did[1]}
    }

    setTotalCount({
      "device": Math.ceil(filteredSearch.device.results.length/ 100),
      "user": Math.ceil(filteredSearch.user.results.length/ 100),
      "did": Math.ceil(filteredSearch.did.results.length/ 100)
    })

    setItem({
      "device": filteredSearch.device.results,
      "user": filteredSearch.user.results,
      "did": filteredSearch.did.results
    })
  }

  const handleSearchTypeChange = e => {
    console.log(display)
    if (e.target.textContent === "Device" && !e.target.style["borderWidth"]) {
      setDisplayStyle({"device": style.searchTypeDeviceStyleHighlight, "user": style.searchTypeUserStyle, "did": style.searchTypeDidStyle})
      setDisplay({"device": true, "user": false, "did": false})
      return
    }

    if (e.target.textContent === "User" && !e.target.style["borderWidth"]) {
      setDisplayStyle({"device": style.searchTypeDeviceStyle, "user": style.searchTypeUserStyleHighlight, "did": style.searchTypeDidStyle})
      setDisplay({"device": false, "user": true, "did": false})
      return
    }

    if (e.target.textContent === "DID" && !e.target.style["borderWidth"]) {  
      setDisplayStyle({"device": style.searchTypeDeviceStyle, "user": style.searchTypeUserStyle, "did": style.searchTypeDidStyleHighlight})
      setDisplay({"device": false, "user": false, "did": true})
      return
    }

    if (e.target.style["borderWidth"]) {
      setDisplayStyle({"device": style.searchTypeDeviceStyle, "user": style.searchTypeUserStyle, "did": style.searchTypeDidStyle})
      setDisplay({"device": true, "user": true, "did": true})
      return
    }
  }

  return (
    <div style={{minWidth: "59.5vw"}}>
      <div style={{display: "flex", minWidth: "59.5vw"}}>
        <div style={{display: "flex"}}>
          <div onClick={e => handleSearchTypeChange(e)} style={displayStyle.device}>Device</div>
          <div onClick={e => handleSearchTypeChange(e)} style={displayStyle.user}>User</div>
          <div onClick={e => handleSearchTypeChange(e)} style={displayStyle.did}>DID</div>
        </div>
        <input style={style.searchInputStyle} onChange={e => handleInputChange(e)}/>
      </div>
      <div >
        <div ref={scrollRef} style={style.listContainerStyle}>
          <div>{renderIndexMenu(display.device, filteredSearch.device.results, filteredSearch.device.relevant, deviceIndex)}</div>
          {renderDeviceList(item.device, display.device)}
          <div>{renderIndexMenu(display.user, filteredSearch.user.results, filteredSearch.user.relevant, userIndex)}</div>
          {renderUserList(item.user, display.user)}
          <div>{renderIndexMenu(display.did, filteredSearch.user.results, filteredSearch.did.relevant, didIndex)}</div>
          {renderDidList(item.did, display.did)}
        </div>
        {/* <Pages total={totalCount}/> */}
      </div>
    </div>
  );
};
