import {useState, useRef} from "react"

let arrow = {
  display: "flex", 
  width: "100%",
  height: "1px",
  backgroundColor: "#4382BC",
  margin: "8px 5px 0 5px",
}

let separator = {
  height: "15px", 
  width: "1px", 
  backgroundColor: "#4382BC", 
  
}

function renderIndent(indent) {
  console.log(Array(indent))
  Array(indent).forEach((item, i) => console.log(item, i))

  // return Array(indent).map(

  // )
}

function renderObject(selector, value, isDown, setIsDown, ref, indent=0) {
  renderIndent(indent)

  if (value === null) return (
    <div 
        onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
        onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}
        style={{display: "flex"}}>
      <div style={{...separator, marginLeft: `${indent*10+3}px`}}></div>
      <div style={{width: "50%", textIndent: `3px`}}>{selector}</div>
      <div style={{width: "50%", direction: "rtl", textIndent: "5px"}}>null</div>
    </div>
  )

  if (typeof(value) === "object") {
    return (
      <div style={{cursor: "pointer"}}>
        <div 
            onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
            onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}
            style={{display: "flex"}}>
          <div style={{...separator, marginLeft: `${indent*10+3}px`}}></div>
          <div style={{textIndent: `3px`}}>{selector}</div>
          {/* <div style={arrow}></div> */}
        </div>
        {function() { 
          return Object.keys(value).map((new_selector, new_ref) => (
            renderObject(new_selector, value[new_selector], isDown, setIsDown, new_ref, indent+1)
          ))
        }()}
      </div>
    )
  }
  
  else return (
    <div 
        onMouseEnter={e => {e.currentTarget.style.backgroundColor = "#b7eef5"}}
        onMouseLeave={e => {e.currentTarget.style.backgroundColor = "white"}}
        style={{display: "flex"}}>
      <div style={{...separator, marginLeft: `${indent*10+3}px`}}></div>
      <div style={{width: "70%", textIndent: "3px"}}>{selector}</div>
      <div style={{width: "30%", direction: "rtl", marginRight: "5px"}}>{value}</div>
    </div>
  )
  
}

//Extends FocusedDataList
export default function FocusedDataItem({itemObject}) {
  const [isDown, setIsDown] = useState(false)
  const [indent, setIndent] = useState(1)
  const arrowRef = useRef(null)
  const objectRef = useRef(null)

  return Object.keys(itemObject).map((item, i) => (
    renderObject(item, itemObject[item], isDown, setIsDown, i)
  ))  
}
